    min-height: 100vh;
    background-color: #F4F6FA;

    .modal {
        position: fixed;
        top: 0;
        z-index: 1001;
        background-color: rgba(0, 0, 0, 0.5);
        width: 100%;
        height: 100%;

        .auth {
            display: none;
        }
    }

    .modal.vk {
        position: fixed;
        top: 0;
        z-index: 1001;
        background-color: rgba(0, 0, 0, 0.5);
        width: 100%;
        height: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;

        .auth {
            display: flex;
            align-items: center;
