    header {
        height: 190px;
        background-color: #5FCBC6;
        .up-header {
            display: flex;
            background-color: #279493;
            align-items: center;
            min-height: 40px;

            .circles {
                margin-left: 5px;
                display: flex;
                flex-direction: row;
                justify-content: space-between;
