                            justify-content: center;
                            align-items: center;
                            left: 5px;
                            width: 5%;
                            height: 30px;
                            border-radius: 12px;
                            background-color: #2DC9BF;
                            cursor: pointer;
                            transition: all .5s;

                            .btn {
                                width: 0; 
                                height: 0; 
                                border-top: 10px solid transparent;
                                border-bottom: 10px solid transparent; 
                                border-right:10px solid #71E7E1;
                            }
