                        .user {
                            justify-content: space-between;
    
                            > * {
                                display: flex;
                                flex-direction: row;
                                align-items: center;
                                justify-content: center;
                                border-radius: 12px;
                                font-size: 20px;
                                height: 100%;
                            }
    
